"use strict";

const lookup = require("./");

const testCases = [
	{
		string: "france", "ISO2": "FR"
	},
	{
		string: "USA", ISO2: "US"
	}
];

describe("Get country code from string", function () {
	for (const testCase of testCases) {
		const country = lookup(testCase.string);
		it("should return " + testCase.ISO2 + " for string \"" + testCase.string + "\"", function () {
			expect(country.getISO2()).toBe(testCase.ISO2);
		});
	}
});
